mapper.select:
SELECT TT.* FROM ( SELECT COUNT(ID) OVER() AS MAX_COUNT, ROW_NUMBER() OVER(ORDER BY PT.ID DESC) AS ROWNUM, PT.ID, PT.TITLE, PT.CONTENT, PT.WRITER, PT.VIEW_CNT, PT.NOTICE_YN, PT.DELETE_YN, PT.CREATE_DATE, PT.MODIFIED_DATE, PT.POST_TYPE, FT.TRANS_FILE_PATH AS FILE_LIST FROM POST_TABLE PT LEFT OUTER JOIN FILE_TABLE FT ON FT.FILE_REFER_KEY = PT.ID WHERE 1=1
AND PT.POST_TYPE = #{searchSec}
) TT WHERE TT.ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex} ORDER BY TT.ROWNUM

mapper.insert:
INSERT 
	INTO POST_TABLE(
		ID,
		TITLE,
		CONTENT,
		WRITER,
		VIEW_CNT,
		NOTICE_YN,
		DELETE_YN,
		CREATE_DATE,
		POST_TYPE
	)VALUES(
		#{id}
		,#{postTitle}
		,#{postContent}
		,#{writer}
		,0
		,0
		,'N'
		,NOW()
		,#{postType}						
	)
